import { connect } from "react-redux";
import {
  follow,
  setCurrentPage,
  setTooggleIsFatching,
  setUsers,
  setUsersTotalCount,
  unfollow,
} from "../redux/friends-reducer";
import AsideFriends from "./Aside_Friends";
import React from "react";
import axios from "axios";
import loading from "./Infinity-2s-223px.gif";

let mapStateToProps = (state) => {
  return {
    FriendsData: state.FriendsPage.FriendsData,
    pageSize: state.FriendsPage.pageSize,
    totalUsersCount: state.FriendsPage.totalUsersCount,
    currentPage: state.FriendsPage.currentPage,
    isFetching: state.FriendsPage.isFetching,
  };
};
class FriendsApiContainer extends React.Component {
  componentDidMount() {
    this.props.setTooggleIsFatching(true);
    axios
      .get(
        `http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${this.props.currentPage}`
      )
      .then((response) => {
        this.props.setTooggleIsFatching(false);
        this.props.setUsers(response.data);
      });
  }
  onpageNumber = (pageNumber) => {
    this.props.setTooggleIsFatching(true);
    this.props.setCurrentPage(pageNumber);
    axios
      .get(
        `http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${pageNumber}`
      )
      .then((response) => {
        this.props.setTooggleIsFatching(false);
        this.props.setUsers(response.data);
      });
  };
  render() {
    return (
      <>
        {this.props.isFetching ? <img alt="" src={loading} /> : null}
        <AsideFriends
          totalUsersCount={this.props.totalUsersCount}
          pageSize={this.props.pageSize}
          currentPage={this.props.currentPage}
          onpageNumber={this.onpageNumber}
          FriendsData={this.props.FriendsData}
          follow={this.props.follow}
          unfollow={this.props.unfollow}
        ></AsideFriends>
      </>
    );
  }
}

const FriendsContainer = connect(mapStateToProps, {
  follow,
  unfollow,
  setUsers,
  setCurrentPage,
  setUsersTotalCount,
  setTooggleIsFatching,
})(FriendsApiContainer);

export default FriendsContainer;
