import logo1 from "../Aside_Friends/1639050807_7-abrakadabra-fun-p-berserk-anime-na-avu-7.jpg";
import logo2 from "../Aside_Friends/a59414ef22bffa0e080627740e7fd6fb05e3a4acr1-1280-960v2_00.jpg";
import logo3 from "../Aside_Friends/ava-steam-92.jpg";


let FOLLOW = "FOLLOW";
let UNFOLLOW = "UNFOLLOW";
let SET_USERS = "SET-USERS";


let initialState= {
    FriendsData:[
        {id:1,img:logo1,followed:true,fullname:"Dmitry",status:"УСИИМ привет",location:{city:'Volcovysk',country:"Belarus"}},
        {id:2,img:logo2,followed:true,fullname:"Maksim",status:"УСИИМ poka",location:{city:'Minsk',country:"Belarus"}},
        {id:3,img:"../Aside_Friends/-1280-.",followed:false,fullname:"Egor",status:"hai hai",location:{city:'Moscow',country:"Russia"}},
    ]
}

const friendsReducer = (state=initialState, action) => {
  debugger;
  switch (action.type) {
    case FOLLOW:{
      return{
        ...state,
        FriendsData:state.FriendsData.map(u=>{
            if(u.id===action.userid){
                return{...u,followed:false}
            }
            return u;
        })  
      };
    }
    case UNFOLLOW:{
     return{
        ...state,
        FriendsData:state.FriendsData.map(u=>{
            if(u.id===action.userid){
                return{...u,followed:true}
            }
            return u;
        })  
      };
    }
    case SET_USERS:{
        return{
            ...state,
            FriendsData:[...state.FriendsData,...action.FriendsData]
        }
    }
    default:
      return state;
  }
};

export let followActionCreator=(userid)=>({
  type:FOLLOW,userid  
})
export let unfollowActionCreator=(userid)=>({
  type:UNFOLLOW,userid  
})
export let setUserActionCreator=(users)=>({
    type:SET_USERS,users
  })

export default friendsReducer;
