let SET_USER_DATA = "SET_USER_DATA";




let initialState= {
    userid:null,
    email:null,
    login:null,
    auth:false
}

const authReducer = (state=initialState, action) => {
  switch (action.type) {
    case SET_USER_DATA:{
        debugger;
      return{
        ...state,
        ...action.data
      };
    }
    default:
      return state;
  }
};

export let setAuthUserData=(userid,email,login)=>({
  type:SET_USER_DATA,data:{userid,login,email} 
})



export default authReducer;
