import React from 'react';
import { addPostActionCreaotor, updateNewPostTextActionCreator } from '../../redux/state';
import NewsOld from './NewsOld';

const NewsOldContainer=(props)=>{
    let state=props.store.getState();

    let addPost=()=>{
        props.store.dispatch(addPostActionCreaotor());
    }  
    let onPostChange=(text)=>{
       let action=updateNewPostTextActionCreator(text)
       props.store.dispatch(action)
    }
    return(
        <NewsOld updateNewPostTextActionCreator={onPostChange} addPost={addPost} NewsData={props.NewsPage.NewsData} newPostText={state.NewsPage.newPostText}></NewsOld>
    )
}

export default NewsOldContainer
