import React from 'react';
import Dialogs from '../../Aside/Dialogs/Dialogs';
import { addPostActionCreaotor, updateNewPostTextActionCreator } from '../../redux/state';
import StoreContext from '../../StoreContext';
import NewsOld from './NewsOld';

const NewsOldContainer=(props)=>{
    return(
        <StoreContext.Consumer>
        {
            (store)=>{
                let state=store.getState();

                let addPost=()=>{
                    store.dispatch(addPostActionCreaotor());
                }  
                let onPostChange=(text)=>{
                   let action=updateNewPostTextActionCreator(text)
                    store.dispatch(action)
                }
                return(
                    <NewsOld updateNewPostTextActionCreator={onPostChange} addPost={addPost} NewsData={state.NewsPage.NewsData} newPostText={state.NewsPage.newPostText}></NewsOld>
                )
            }
      
        }
        </StoreContext.Consumer>
       
    )

}

let mapStateToProps=(state)=>{
    return{
        NewsData:state.NewsPage.NewsData,
        newPostText:state.NewsPage.newPostText
    }   
}

let mapDispatchToProps=(dispatch)=>{
    return{
        updateNewPostTextActionCreator:(text)=>{
            let action=updateNewPostTextActionCreator(text)
            dispatch(action)
        },
        addPost:()=>{
            dispatch(addPostActionCreaotor())
        }
    }
}

const superNewsOldContainer=connect(mapStateToProps,mapStateToProps)(NewsOld)

export default NewsOldContainer
