import { connect } from 'react-redux';
import { followActionCreator, setUserActionCreator, unfollowActionCreator } from '../redux/friends-reducer';
import AsideFriends from './Aside_Friends';

let mapStateToProps=(state)=>{
    return{
        FriendsData:state.FriendsPage.FriendsData,
        pageSize:state.FriendsPage.pageSize,
        totalUsersCount:state.FriendsPage.totalUsersCount,
        currentPage:state.FriendsPage.currentPage
    }   
}

let mapDispatchToProps=(dispatch)=>{
    return{
        follow:(userid)=>{
            dispatch(followActionCreator(userid))
        },
        unfollow:(userid)=>{
            dispatch(unfollowActionCreator(userid))
        },
        setUsers:(users)=>{
            dispatch(setUserActionCreator(users))
        }
    }
}

const FriendsContainer=connect(mapStateToProps,mapDispatchToProps)(AsideFriends)

export default FriendsContainer
