let ADD_POST = "ADD-POST";
let UPDATE_POST = "UPDATE-POST";

let initialState= {
    NewsData:[
      {post:"всё гуд"},
      {post:"всё супер гуд"},
      {post:"всё мега класс"},
      {post:"всё мега класс"},
    ], 
    newPostText:'' 
}

const newsReducer = (state=initialState, action) => {
  switch (action.type) {
    case ADD_POST:{
      let newPost = {
        post: state.newPostText,
      };
      let stateCopy={
        ...state,
        newPostText:"",
        NewsData
      };
      stateCopy.NewsData=[...state.NewsData]
      stateCopy.NewsData.push(newPost);
      stateCopy.newPostText = "";
      return stateCopy;
    }
    case UPDATE_POST:{
      let stateCopy={...state};
      stateCopy.NewsData=[...state.NewsData]
      stateCopy.newPostText = action.newText;
      return stateCopy;
    }
    default:
      return state;
  }
};

export let addPostActionCreaotor=()=>({
  type:ADD_POST   
})
export let updateNewPostTextActionCreator=(text)=>({
  type:UPDATE_POST,newText:text
})

export default newsReducer;
