import { connect } from 'react-redux';
import { followActionCreator, setCurrentPageActionCreator, setUserActionCreator, setUsersTotalCountActionCreator, unfollowActionCreator } from '../redux/friends-reducer';
import AsideFriends from './Aside_Friends';
import React from "react";
import axios from "axios";

let mapStateToProps=(state)=>{
    return{
        FriendsData:state.FriendsPage.FriendsData,
        pageSize:state.FriendsPage.pageSize,
        totalUsersCount:state.FriendsPage.totalUsersCount,
        currentPage:state.FriendsPage.currentPage
    }   
}

let mapDispatchToProps=(dispatch)=>{
    return{
        follow:(userid)=>{
            dispatch(followActionCreator(userid))
        },
        unfollow:(userid)=>{
            dispatch(unfollowActionCreator(userid))
        },
        setUsers:(users)=>{
            dispatch(setUserActionCreator(users))
        },
        setCurrentPage:(pageNumber)=>{
            dispatch(setCurrentPageActionCreator(pageNumber))
        },
        setUsersTotalCount:(currentTotalUsers)=>{
            dispatch(setUsersTotalCountActionCreator(currentTotalUsers))
        }
    }
}
class FriendsApiContainer extends React.Component {
    componentDidMount() {
      axios
        .get(
          `http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${this.props.currentPage}`
        )
        .then((response) => {
          this.props.setUsers(response.data);
        });
    }
    onpageNumber = (pageNumber) => {
      this.props.setCurrentPage(pageNumber);
      axios
        .get(
          `http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${pageNumber}`
        )
        .then((response) => {
          this.props.setUsers(response.data);
        });
    };
    render() {
      return (
        <>
        <AsideFriends
          totalUsersCount={this.props.totalUsersCount}
          pageSize={this.props.pageSize}
          currentPage={this.props.currentPage}
          onpageNumber={this.onpageNumber}
          FriendsData={this.props.FriendsData}
          follow={this.props.follow}
          unfollow={this.props.unfollow}
        ></AsideFriends>
        </>
      );
    
    }
  }

const FriendsContainer=connect(mapStateToProps,mapDispatchToProps)(FriendsApiContainer)

export default FriendsContainer
