import "./Aside_Friends.scss";
import React from "react";
import axios from "axios";
import logo from "./ava-steam-92.jpg";

class AsideFriends extends React.Component {
  componentDidMount() {
    axios.get("http://localhost:3001/data").then((response) => {
      this.props.setUsers(response.data);
    });
  }
  render() {
    let pagesCount = this.props.totalUsersCount / this.props.pageSize;

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
      pages.push(i);
    }
    return (
      <div>
        <div>
          {pages.map((p) => {
            <span>{p}</span>;
          })}
        </div>
        {this.props.FriendsData.map((u) => (
          <div key={u.id} className="Aside_Friends">
            <div className="friends_button">
              <img src={logo} alt="" />
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      this.props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      this.props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                )}
              </div>
            </div>
            <div className="friends_content">
              <div className="friends_content_column">
                <p>Имя:{u.first_name}</p>
                <p>Фамилия:{u.last_name}</p>
              </div>
              <div className="friends_content_column"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default AsideFriends;
