import "./Aside_Friends.scss"
import React from "react";
import axios from "axios";
import logo from "./ava-steam-92.jpg";

class AsideFriends extends React.Component{
    constructor(props){
        super(props);
        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response=>{
            props.setUsers(response.data.items)
            console.log()
        })
        }
    render(){
    return(
        <div>{
            this.props.FriendsData.map(u=><div key={u.id} className="Aside_Friends">
                <div className="friends_button">
                <img src={logo} alt="" />
                <div>
                {u.followed
                    ?<button onClick={()=>{this.props.unfollow(u.id)}}>Unfollow</button>
                    :<button onClick={()=>{this.props.follow(u.id)}}>Follow</button>
                    }
                </div>
                </div>
                <div className="friends_content">
                <div className="friends_content_column">
                <p>Имя:{u.name}</p>
                <p>Статус:{u.status}</p>
                </div>
                <div className="friends_content_column">
                </div>
                </div>
                </div>
            )
        }
      </div>
    )
    }
}

export default AsideFriends
