let FOLLOW = "FOLLOW";
let UNFOLLOW = "UNFOLLOW";
let SET_USERS = "SET_USERS";
let SET_CURRENT_PAGE="SET_CURRENT_PAGE";
let SET_USERS_TOTAL_COUNT="SET_USERS_TOTAL_COUNT";
let TOOGLE_ISFETCHING="TOOGLE_ISFETCHING"


let initialState= {
    FriendsData:[],
    pageSize:5,
    totalUsersCount:25,
    currentPage:1,
    isFetching:false
}

const friendsReducer = (state=initialState, action) => {
  switch (action.type) {
    case FOLLOW:{
      return{
        ...state,
        FriendsData:state.FriendsData.map(u=>{
            if(u.id===action.userid){
                return{...u,followed:false}
            }
            return u;
        })  
      };
    }
    case UNFOLLOW:{
     return{
        ...state,
        FriendsData:state.FriendsData.map(u=>{
            if(u.id===action.userid){
                return{...u,followed:true}
            }
            return u;
        })  
      };
    }
    case SET_USERS:{
        return{...state,FriendsData:[...action.users]}
    }
    case SET_CURRENT_PAGE:{
        return{...state,currentPage:action.currentPage}
    }
    case SET_USERS_TOTAL_COUNT:{
      return{...state,totalUsersCount:action.currentTotalUsersCount}
  }
    case TOOGLE_ISFETCHING:{
      return{...state,isFetching:action.isFetching}
  }
    default:
      return state;
  }
};

export let followActionCreator=(userid)=>({
  type:FOLLOW,userid  
})
export let unfollowActionCreator=(userid)=>({
  type:UNFOLLOW,userid  
})
export let setUserActionCreator=(users)=>({
    type:SET_USERS,users
  })

export let setCurrentPageActionCreator=(currentPage)=>({
    type:SET_CURRENT_PAGE,currentPage
  })

export let setUsersTotalCountActionCreator=(currentTotalUsersCount)=>({
    type:SET_USERS_TOTAL_COUNT,currentTotalUsersCount
  })
export let setTooggleIsFatchingActionCreator=(isFetching)=>({
    type:TOOGLE_ISFETCHING,isFetching
  })



export default friendsReducer;
