let ADD_POST = "ADD-POST";
let UPDATE_POST = "UPDATE-POST";

let initialState= {
    NewsData:[
      {post:"всё гуд"},
      {post:"всё супер гуд"},
      {post:"всё мега класс"},
      {post:"всё мега класс"},
    ], 
    newPostText:'' 
}

const newsReducer = (state=initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      let newPost = {
        post: state.newPostText,
      };
      state.NewsData.push(newPost);
      state.newPostText = "";
      return state;
    case UPDATE_POST:
      state.newPostText = action.newText;
      return state;
    default:
      return state;
  }
};

export default newsReducer;
