let FOLLOW = "FOLLOW";
let UNFOLLOW = "UNFOLLOW";
let SET_USERS = "SET-USERS";

let initialState= {
    FriendsData:[
        {id:1,followed:true,fullname:"Dmitry",status:"УСИИМ привет",location:{city:'Volcovysk',country:"Belarus"}},
        {id:2,followed:false,fullname:"Maksim",status:"УСИИМ poka",location:{city:'Minsk',country:"Belarus"}},
        {id:3,followed:true,fullname:"Egor",status:"hai hai",location:{city:'Moscow',country:"Russia"}},
    ]
}

const friendsReducer = (state=initialState, action) => {
  switch (action.type) {
    case FOLLOW:{
      return{
        ...state,
        friendsData:state.friendsData.map(u=>{
            if(u.id===action.type){
                return{...u,followed:true}
            }
            return u;
        })  
      };
    }
    case UNFOLLOW:{
     return{
        ...state,
        friendsData:state.friendsData.map(u=>{
            if(u.id===action.type){
                return{...u,followed:false}
            }
            return u;
        })  
      };
    }
    case SET_USERS:{
        return{
            ...state,
            users:[...state.users,...action.users]
        }
    }
    default:
      return state;
  }
};

export let followActionCreator=(userid)=>({
  type:FOLLOW,userid  
})
export let unfollowActionCreator=(userid)=>({
  type:UNFOLLOW,userid  
})
export let setUserActionCreator=(users)=>({
    type:SET_USERS,users
  })

export default friendsReducer;
