import React from 'react';
import "./Aside_News.scss"
import logo from "./382c5195604ff36d7a369206280a342d.jpg"
let textarea=React.createRef();

let RewiewPost=()=>{
    let text=textarea.current.value;
    alert(text);
}
let News
const Aside_News=(props)=>{
    return(
        <div className="Aside_News">
        <div className="News_Add">
        <h1>Новости</h1>
        <textarea ref={textarea}></textarea>
        <button onClick={RewiewPost}></button>
        </div>
        <div className="News_Old">
        <div className="News_Old_row">
        <img src={logo} alt="" />
        <p>всё супер всё гуд</p>
        </div>
        <div className="News_Old_row">
        <img src={logo} alt="" />
        <p>всё мега гуд</p>
        </div>
        </div>
        </div>
    )
}

export default Aside_News
