import "./Aside_Friends.scss"
import axios from "axios";
import logo from "./ava-steam-92.jpg";

const AsideFriends=(props)=>{
    if(props.FriendsData.length===0){
        debugger;
    //    props.setUsers([
    //     {id:1,img:logo1,followed:true,fullname:"Dmitry",status:"УСИИМ привет",location:{city:'Volcovysk',country:"Belarus"}},
    //     {id:2,img:logo2,followed:true,fullname:"Maksim",status:"УСИИМ poka",location:{city:'Minsk',country:"Belarus"}},
    //     {id:3,img:logo3,followed:false,fullname:"Egor",status:"hai hai",location:{city:'Moscow',country:"Russia"}},
    //    ])
    axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response=>{
        props.setUsers(response.data.items)
    })
    }

    return(
        <div >{
            props.FriendsData.map(u=><div key={u.id} className="Aside_Friends">
                <div className="friends_button">
                <img src={logo} alt="" />
                <div>
                {u.followed
                    ?<button onClick={()=>{props.unfollow(u.id)}}>Unfollow</button>
                    :<button onClick={()=>{props.follow(u.id)}}>Follow</button>
                    }
                </div>
                </div>
                <div className="friends_content">
                <div className="friends_content_column">
                <p>Имя:{u.name}</p>
                <p>Статус:{u.status}</p>
                </div>
                <div className="friends_content_column">
                </div>
                </div>
                </div>
            )
        }
      </div>
    )
}

export default AsideFriends
