let ADD_POST = "ADD-POST";
let UPDATE_POST = "UPDATE-POST";

let initialState={
    
}

const newsReducer = (state, action) => {
  switch (action.type) {
    case ADD_POST:
      let newPost = {
        post: state.newPostText,
      };
      state.NewsData.push(newPost);
      state.newPostText = "";
      return state;
    case UPDATE_POST:
      state.newPostText = action.newText;
      return state;
    default:
      return state;
  }
};

export default newsReducer;
