let SEND_MESSAGE = "SEND-MESSAGE";
let UPDATE_MESSAGE = "UPDATE-MESSAGE";

let initialState={
        NewsData:[
          {post:"всё гуд"},
          {post:"всё супер гуд"},
          {post:"всё мега класс"},
          {post:"всё мега класс"},
        ], 
        newPostText:''  
      }

const messagesReducer = (state, action) => {
  switch (action.type) {
    case SEND_MESSAGE:
      let body = state.newMessageBody;
      state.MessagesData.push({ id: 3, message: body });
      state.newMessageBody = "";
      return state;
    case UPDATE_MESSAGE:
      state.newMessageBody = action.body;
      return state;
    default:
      return state;
  }
};

export default messagesReducer;
