import React from 'react';
import "./Aside_News.scss"
import logo from "./382c5195604ff36d7a369206280a342d.jpg"
let textarea=React.createRef();

let RewiewPost=()=>{
    let text=textarea.current.value;
    alert(text);
}
let NewsPost=(props)=>{
    return(
    <div>
    <img src={logo} alt="" />
    <p>{props.post}</p>
    </div>
    );
}
const AsideNews=(props)=>{
    let NewsPosts=props.NewsData.map(post=><NewsPost post={post.post}></NewsPost>)
    return(
        <div className="Aside_News">
        <div className="News_Add">
        <h1>Новости</h1>
        <textarea ref={textarea}></textarea>
        <button onClick={RewiewPost}></button>
        </div>
        </div>
    )
}

export default AsideNews
