import styles from "./Aside_Friends.module.scss";
import React from "react";
import axios from "axios";
import logo from "./ava-steam-92.jpg";

class AsideFriends extends React.Component {
  componentDidMount() {
    axios.get(`http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${this.props.currentPage}`).then((response) => {
      this.props.setUsers(response.data);
      this.props.setUsersTotalCount(response.data.t)
    });
  }
  onpageNumber=(pageNumber)=>{
    this.props.setCurrentPage(pageNumber)
    axios.get(`http://localhost:3001/data?_limit=${this.props.pageSize}&_page=${pageNumber}`).then((response) => {
        this.props.setUsers(response.data);
      });
  }
  render() {
    let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
      pages.push(i);
    }
    return (
      <div>
        <div className={styles.CurrentPageWrap}>
          {pages.map(p => {
           return <span onClick={(e)=>{this.onpageNumber(p)}} className={this.props.currentPage=== p && styles.CurrentPage}>{p}</span>
          })}
        </div>
        {this.props.FriendsData.map((u) => (
          <div key={u.id} className={styles.Aside_Friends}>
            <div className={styles.friends_button}>
              <img src={logo} alt="" />
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      this.props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      this.props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                )}
              </div>
            </div>
            <div className={styles.friends_content}>
              <div className={styles.friends_content_column}>
                <p>Имя:{u.first_name}</p>
                <p>Фамилия:{u.last_name}</p>
              </div>
              <div className={styles.friends_content_column}></div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default AsideFriends;
